Source.BuildSpec:
{
  "version": "0.2",
  "phases": {
    "build": {
      "commands": [
        "cd mubarizkhan/sprint5_mubariz",
        "pip install -r requirements.txt",
        "npm install -g aws-cdk",
        "cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "mubarizkhan/sprint5_mubariz/cdk.out",
    "files": "**/*"
  }
}

{
  "version": "0.2",
  "phase": {
    "install": {
      "commands": [
        "nohup /usr/local/bin/dockerd --host=unix:///var/run/docker.sock --host=tcp://127.0.0.1:2375 --storage-driver=overlay2 &",
        "timeout 15 sh -c \"until docker info; do echo .; sleep 1; done\""
      ]
    },
    "pre_build": {
      "commands": [
        "ls && cd sprint5_mubariz/pyrest",
        "docker build -t api-mak2 ."
      ]
    },
    "build": {
      "commands": [
        "docker images",
        "docker run  api-mak2 https://4ifp46i0ie.execute-api.us-west-1.amazonaws.com api_test.yml"
      ]
    }
  },
  "phases": {}
}
